import { Component, ElementRef, Renderer2, ViewChild } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  isSlideOutVisible = false;

  @ViewChild('slideOutDiv', { static: false }) slideOutDiv!: ElementRef;

  constructor(private renderer: Renderer2) {}

  toggleSlideOut() {
    this.isSlideOutVisible = !this.isSlideOutVisible;

    if (this.slideOutDiv) {
      if (this.isSlideOutVisible) {
        this.renderer.addClass(this.slideOutDiv.nativeElement, 'show-slide-out');
      } else {
        this.renderer.removeClass(this.slideOutDiv.nativeElement, 'show-slide-out');
      }
    }
  }
}
<!-- navbar.component.html -->
<div class="container-fluid"> <!-- Use container-fluid to make the main navbar full width -->

  <!-- Main Navbar -->
  <nav class="navbar navbar-expand-md bg-dark navbar-dark">
    <!-- Your Brand/Logo/Title Goes Here -->
    <a class="navbar-brand" href="#">
      <img id="nav-logo" src="https://i.ibb.co/tLSJPhD/Fitchit-removebg-preview.png" alt="Logo">
    </a>

    <!-- Button to toggle the slide-out div -->
    <button class="btn ml-auto" (click)="toggleSlideOut()" id="toggleBtn">
      <i class="fa-solid fa-bars"></i>
    </button>
  </nav>

  <!-- Slide-out div with vertical navbar -->
  <div #slideOutDiv class="slide-out-div" [ngClass]="{'show-slide-out': isSlideOutVisible}">
    <h4 id="slide-top-heading">Hi! User</h4>
    <img id="slide-bar-img" src="https://img.freepik.com/free-icon/user_318-159711.jpg" alt="User Image">
    <h6 id="slide-bottom-designation">Designation</h6>
    <button id="slide-bottom-logout" class="btn btn-dark">Log out</button>
  </div>

</div>

<link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css" integrity="sha512-iecdLmaskl7CVkqkXNQ/ZH/XLlvWZOJyj7Yy7tcenmpD1ypASozpmT/E0iPtmFIB46ZmdtAc9eNBvH0H/ZpiBw==" crossorigin="anonymous" referrerpolicy="no-referrer" />
<script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js"></script>
<script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
