<head>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
  <title>Login Page</title>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">
  <style>
      /* General styling for the login form */
      .login-form {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 10px;
          max-width: 300px;
          margin: 0 auto;
      }

      /* Styling for the input fields */
      .login-form input {
          padding: 10px;
          border: none;
          border-radius: 5px;
          background-color: #f0f0f0;
          transition: background-color 0.3s, border-color 0.3s;
      }

      /* Styling for the labels */
      .login-form label {
          font-weight: bold;
      }

      /* Remove the border box on focus for input fields */
      .login-form input:focus {
          outline: none;
          border-color: #6c63ff;
      }

      /* Lighter color on hover for input fields */
      .login-form input:hover {
          background-color: #f5f5f5;
          cursor: pointer;
      }

      /* Styling for the submit button */
      .login-form input[type="submit"] {
          padding: 10px 20px;
          border: none;
          border-radius: 5px;
          background-color: #6c63ff;
          color: #fff;
          font-weight: bold;
          cursor: pointer;
      }

      /* Lighter color on hover for submit button */
      .login-form input[type="submit"]:hover {
          background-color: #534fd6;
      }
  </style>
</head>

<body>
  <section class="vh-220 p-5">
      <div class="container h-100">
          <div class="row d-flex justify-content-center align-items-center h-100">
              <div class="col-lg-12 col-xl-11">
                  <div class="card text-white" style="border-radius: 25px;">
                      <div class="card-body p-md-5" style="background-color: #000000;">
                          <div class="row justify-content-center">
                              <div class="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1" style="margin-top: 6%;">
                                  <p class="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Login</p>
                                  <form class="mx-1 mx-md-4" action="Employee.html">
                                      <div class="d-flex flex-row align-items-center mb-5">
                                          <i class="fa fa-envelope fa-lg me-3 fa-fw mb-4"></i>
                                          <div class="form-outline flex-fill mb-0">
                                              <input type="email" id="form3Example3c" class="form-control" required>
                                              <label class="form-label" for="form3Example3c">Your Email</label>
                                          </div>
                                      </div>
                                      <div class="d-flex flex-row align-items-center mb-4">
                                          <i class="fa fa-lock custom-in-between-size me-3 fa-fw mb-4"></i>
                                          <div class="form-outline flex-fill mb-0">
                                              <input type="password" id="form3Example4c" class="form-control" required>
                                              <label class="form-label" for="form3Example4c">Password</label>
                                          </div>
                                      </div>
                                      <div class="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                                          <a href="Employee.html"><button type="submit" class="btn btn-light btn-lg"
                                                  id="login">Login</button></a>
                                      </div>
                                  </form>
                              </div>
                              <div class="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2">
                                  <img src="https://image.freepik.com/free-vector/mobile-login-concept-illustration_114360-2650.jpg"
                                      class="img-fluid" alt="Sample image">
                              </div>
                          </div>
                      </div>
                  </div>
              </div>
          </div>
      </div>
  </section>
</body>
